mapper.update:
UPDATE Users
SET password    = #{user.newPassword},
    profile_img = NULLIF(#{user.profileImg}, ''),
    email       = #{user.email},
    nickname    = #{user.nickname}
WHERE id = #{userId}

update users
set token = #{refreshToken}
where id = #{userId}

mapper.select:
select token
from users
where id = #{userId}

mapper.update:
update users
set token = ''
where id = #{userId}

mapper.select:
SELECT COUNT(*)
FROM Users
WHERE id = #{userId}

SELECT COUNT(*)
FROM Users
WHERE nickname = #{nickname}

mapper.update:
UPDATE users
SET status = 'INACTIVATE'
WHERE id = #{user.id}
